script:
/* 1.1 - Normalize middle_school data */

middle_school := middle_school
    [rename rentree_scolaire to year, numero_du_college to school_id, nombre_d_eleves_total to student_number]
    [calc identifier year := year, identifier school_id := school_id, student_number := cast(student_number, integer)];

/* 1.2 - Normalize school data */

school := school
    [rename rentree_scolaire to year, numero_de_l_ecole to school_id, nombre_total_d_eleves to student_number]
    [calc identifier year := year, identifier school_id := school_id, student_number := cast(student_number, integer)];

/* 1.3 - Normalize middle_school data */

general_high_school := general_high_school
    [rename rentree_scolaire to year, numero_du_lycee to school_id, nombre_d_eleves to student_number]
    [calc identifier year := year, identifier school_id := school_id, student_number := cast(student_number, integer)];

/* 1.4 - Normalize middle_school data */

professional_high_school := professional_high_school
    [rename rentree_scolaire to year, numero_du_lycee to school_id, nombre_d_eleves to student_number]
    [calc identifier year := year, identifier school_id := school_id, student_number := cast(student_number, integer)];

/* 1.5 - Normalize nature_mapping data */

nature_mapping := nature_mapping
    [drop nature]
    [calc identifier code_nature := code_nature];

/* 1.6 - Normalize school_registry data */
school_registry := school_registry
    [calc identifier school_id := school_id, latitude := cast(latitude, number), longitude := cast(longitude, number),
        institution_type := if institution_type = "Public" then "PU" else (
                                            if institution_type = "Priv√©"
                                            then "PR"
                                            else cast(null, string)
                                            ),
        arm := if lau in {"75101", "75102", "75103", "75104", "75105", "75106", "75107", "75108", "75109", "75110", "75111", "75112", "75113", "75114", "75115", "75116", "75117", "75118", "75119", "75120"}
                            then "75056"
                            else (
                                if lau in {"69381", "69382", "69383", "69384", "69385", "69386", "69387", "69388", "69389"}
                                then "69123"
                                else (
                                    if lau in {"13201", "13202", "13203", "13204", "13205", "13206", "13207", "13208", "13209", "13210", "13211","13212", "13213", "13214", "13215", "13216"}
                                    then "13055"
                                    else cast(null, string)
                                )
                            )]
        ;

/* 2.1 - Union student data */

students := union(middle_school, school, general_high_school, professional_high_school);

/* 2.2 Join nature_mapping in school_registry */

school_registry := school_registry[calc measure school_id := school_id, identifier code_nature := code_nature];
schools := left_join(school_registry, nature_mapping)
                [calc identifier school_id := school_id, measure code_nature := code_nature];

/* 3 - Join students in schools*/

students := students [calc measure year := year];
school_students := left_join(schools, students);
